import React from 'react'
import styled from 'styled-components'
import Globe from '../../assets/earth.json'
import Lottie from 'react-lottie'
const Nav = styled.nav`
display:flex;
flex-direction:row;
flex-wrap:wrap;
justify-content:space-between;
font-weight:200;
font-size:45px;
margin-bottom:20px;
color:black;
.Logo{
    display : flex;
    flex-wrap : wrap;
    flex-direction : row;
    width : 210px;
    justify-content : space-between;
}`
const Dates = styled.span`
font-size:27px;`
export const CurrentDate = () =>
{   const months= ["January","February","March","April","May","June","July","August","September","October","November","December"];
    const days=["Sunday","Monday","Tuesday","Wedensday","Thursday","Friday","Saturday"]
    return(
    <>
        <span>{days[new Date().getDay()]}, {new Date().getDate()}<sup>th</sup></span>
        <Dates>{months[new Date().getMonth()]} {new Date().getFullYear()}</Dates>
    </>
    )
}
const Header = () => {
    const defaultOptions = {
        loop: true,
        autoplay: true,
        animationData: Globe,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
      }
    return (
        <Nav>
            <div className="Logo">
                <div><Lottie options={defaultOptions} height={50} width={40} /></div>
                <div>Cov-Live</div>
            </div>
            <div className="Date"><CurrentDate /></div>
        </Nav>
    )
}

export default Header
