import React, { useEffect, useState } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'
import CardGraph from './CardGraph/CardGraph'

const GlobalData = () => {
    const [GlobalInfo,setData] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        covidURL.get(`/${date}/country/India`)
        .then(resp =>
            {
                setData(resp.data.total)
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    const Cards=[
        {
            styles:"linear-gradient(to right, #e53935, #e35d5b)",
            CardFront:{
                type:"Total",
                info: GlobalInfo.today_confirmed,
                infoRadial: GlobalInfo.today_confirmed
            }
        },
        {
            styles:"linear-gradient(to right, #ff512f, #f09819)",
            CardFront:{
                type:"Active",
                info: GlobalInfo.today_open_cases,
                infoRadial: Math.round((GlobalInfo.today_open_cases/GlobalInfo.today_confirmed)*100)
            }
        },
        {
            styles:"linear-gradient(to right, #4cb8c4, #3cd3ad)",
            CardFront:{
                type:"Recovered",
                info: GlobalInfo.today_recovered,
                infoRadial: Math.round((GlobalInfo.today_recovered/GlobalInfo.today_confirmed)*100)
            }
        },
        {
            styles:"linear-gradient(to right, #606c88, #3f4c6b)",
            CardFront:{
                type:"Deaths",
                info: GlobalInfo.today_deaths,
                infoRadial: Math.round((GlobalInfo.today_deaths/GlobalInfo.today_confirmed)*100)
            }
        }
    ]
    const Globals = styled.div`
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    justify-content:space-between;
    `
    const Wrapper= styled.div`
      perspective:1000rem;
      position:relative;
      transition:0.8s all;
      height:400px;
      width:400px;
      margin-bottom:20px;
    `
    const Card= styled.div`
    width:100%;
    height:100%;
    position:absolute;
    backface-visibility:hidden;
    transform-style:preserve-3d;
    transition:1s all;
    &:active{
        transform: rotateY(180deg);
    }
    `
    const Front = styled.div`
    position:absolute;
    backface-visibility:hidden;
    padding:15px;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    border-radius:0.5rem;
    `

    const Back = styled.div`
    position:absolute;
    backface-visibility:hidden;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    padding:15px;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    transform:rotateY(-180deg);
    border-radius:0.5rem;
    background:yellow;
    `
    return (
        <Globals>
            {   
                Cards.map((each,index) =>
                (   

                    <Wrapper key={index}>
                        <Card>
                            <Front style={{background:each.styles}}>
                                {/*<CardGraph 
                                Infos={each.CardFront.info} 
                                Labels={each.CardFront.type} 
                                chartRadial={each.CardFront.infoRadial}/>*/}
                            </Front>
                            <Back style={{background:each.styles}}>Back</Back>
                        </Card>
                    </Wrapper>
                ))
            }
        </Globals>
    )
}

export default GlobalData
