import React,{ useState } from 'react'
import Chart from 'react-apexcharts'

const CardGraph = () => {
    const [chartData] = useState(
        {
            options :
            {
                height: 280,
                type: "radialBar",
            },
            series: [67],
            colors: ["pink"],
            plotOptions:{
                radialBar:{
                    hollow:{
                        margin: 0,
                        size: "70%",
                        background: "#ffffff"
                    },
                    track:{
                        dropShadow: {
                            enabled: true,
                            top: 2,
                            left: 0,
                            blur: 4,
                            opacity: 0.15
                          }
                    },
                    dataLabels: {
                        name: {
                          offsetY: -10,
                          color: "#fff",
                          fontSize: "13px"
                        },
                        value: {
                          color: "#fff",
                          fontSize: "30px",
                          show: true
                        }
                    }
                }
            },
            fill: {
                type: "gradient",
                gradient: {
                  shade: "dark",
                  type: "vertical",
                  gradientToColors: ["#87D4F9"],
                  stops: [0, 100]
                }
              },
              stroke: {
                lineCap: "round"
              },
              labels: ["Progress"]
        },
    )
    return (
        <div>
            <Chart options={chartData.options} series={chartData.series}width="380"/>
        </div>
    )
}

export default CardGraph
