import React, { useState } from 'react'
import Charts from 'react-apexcharts'

const CardGraph = () => {
    const [Data] = useState(
        {
           options : {
               chart:{
                   type:'donut',
                   brush: {
                    enabled: false,
                    target: undefined,
                    autoScaleYaxis: false
                  }
               },
               dataLabels:{
                    enabled:false
                },
                plotOptions: {
                    pie: {
                      customScale: 0.9,
                      donut: {
                        size: '85%',
                        background:'transparent',
                        labels:{
                            show:false,
                            name:{
                                show:true,
                                fontSize:"16px",
                                fontFamily:"Montserrat",
                                offsetY:-10
                            }
                        }
                      }
                    }
                }
           },
           series: [11,45,79],
           chartOptions:{
                labels:['Deaths','population','life']
           }
        }
    )
    return (
        <div>
              <Charts options={Data.options} series={Data.series}  width="350" type='donut' />
        </div>
    )
}

export default CardGraph
