import React from 'react'
import ReactApexChart from 'react-apexcharts'

const Donut = ({GraphData}) => {

    const series = [509343, 197736, 295918, 15689]
    const options = {
        colors: ['#e54443', '#f46527', '#56CEB1', '#3F6484'],
        legend: {
            show : false
          },
          chart: {
            type: 'donut',
            dropShadow: {
                enabled: true,
                top: 30,
                left: -2,
                blur: 10,
                opacity: 0.12
              },
              offsetX : 0,
              offsetY : 0
          },
          labels:['Total','Active','Recovered','Deaths'],
          plotOptions :{
            pie : {
                startAngle : 0,
                offsetX : 40,
                offsetY : 0,
                customScale : 0.95,
            },
            donut:{
              background : 'transparent',
              labels : false
            }
          },
          title: {
            text: 'Fatality',
            align: 'left'
          },
          subtitle: {
            text: '',
            align: 'left'
          },
          dataLabels : {
            enabled : false,
          },
          stroke : {
            show : false,
          },
          fill : {
            colors: ['#e54443', '#f46527', '#56CEB1', '#3F6484']
          },
          responsive: [{
            breakpoint: 480,
            options: {
              chart: {
                width: "100%",
                toolbar :{
                    show:false
                }
              },
              dataLabels : {
                  enabled : false
              },
            }
          }]
        }
    return (
        <ReactApexChart options={options} series={series} type="donut" width="100%" height="100%"/>
    )
}

export default Donut
