import React from 'react'
import styled from 'styled-components'
import Lottie from 'react-lottie'
import staySafe from '../../assets/shss.json'
import WashH from '../../assets/Med.webp'

const Precaution = () => {
    const defaultOptions = {
        loop: true,
        autoplay: true,
        animationData: staySafe,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
      }
      const PrecautionData = [
          {
              point : WashH,
              head : 'Sanitizing and extra care',
              description : "Clean your hands often. Use soap and water, or an alcohol-based hand rub.Don’t touch your eyes, nose or mouth."
          }
      ]
    return (
      <PreContainer>
          <div className="Animation">
              <Lottie options={defaultOptions} height={400} width={500}/>
          </div>
          <div className="Precautions">
              {
                  PrecautionData.map((each,id) =>
                  (
                    <div key={id}>
                        <div>{each.point}</div>
                        <div>{each.head}</div>
                        <div>{each.description}</div>
                    </div>
                    ))
              }
          </div>
      </PreContainer>
    )
}

export default Precaution

const PreContainer = styled.div`
display : flex;
flex-wrap : wrap;
flex-flow : row;
justify-content : center;
width : 100%;
& > div
{
    width : 100%;
    height : 100%;
}
.Animation{
    flex: 1.2;
}
.Precautions{
    flex : 2.0;
    background : purple;
}`
