import React, { useEffect, useState } from 'react'
import { GlobalUrl } from '../../axios'
import styled from 'styled-components'
//import CardGraph from './CardGraph/CardGraph'

const GlobalData = () => {
    const [GlobalInfo,setData] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        GlobalUrl.get(``)
        .then(resp =>
            {
                setData(resp.data.total)
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    const Cards=[
        {
            styles:"linear-gradient(to right, #e53935, #e35d5b)",
            CardFront:{
                type:"Total"
            },
            CardBack:{

            }
        },
        {
            styles:"linear-gradient(to right, #ff512f, #f09819)",
            CardFront:{
                type:"Active",
            },
            CardBack:{

            }
        },
        {
            styles:"linear-gradient(to right, #4cb8c4, #3cd3ad)",
            CardFront:{
                type:"Recovered",

            },
            CardBack:{

            }
        },
        {
            styles:"linear-gradient(to right, #606c88, #3f4c6b)",
            CardFront:{
                type:"Deaths",

            },
            CardBack:{
                Name1:"Yesterday's Total Deaths",
                Name2:"YesterdayToday Ratio",
                YesterdayCases : GlobalInfo.yesterday_deaths,
                YesVStodRatio : GlobalInfo.today_vs_yesterday_deaths,
                SourceProvider: GlobalInfo.source,
                UpdatedAt: GlobalInfo.updated_at  
            }
        }
    ]
    const Globals = styled.div`
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    justify-content:space-between;
    `
    const Wrapper= styled.div`
      perspective:1000rem;
      position:relative;
      transition:0.8s all;
      height:400px;
      width:400px;
      margin-bottom:20px;
    `
    const Card= styled.div`
    width:100%;
    height:100%;
    position:absolute;
    backface-visibility:hidden;
    transform-style:preserve-3d;
    transition:1s all;
    &:active{
        transform: rotateY(180deg);
    }
    `
    const Front = styled.div`
    position:absolute;
    backface-visibility:hidden;
    padding:15px;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    border-radius:0.5rem;
    `

    const Back = styled.div`
    position:absolute;
    backface-visibility:hidden;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    padding:15px;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:space-between;
    transform:rotateY(-180deg);
    border-radius:0.5rem;
    h4
    {
        font-weight: 700;
    }
    div
    {

    }
    `
    return (
        <Globals>
            {   
                Cards.map((each,index) =>
                (   

                    <Wrapper key={index}>
                        <Card>
                            <Front style={{background:each.styles}}>
                                {/*<CardGraph 
                                Infos={each.CardFront.info} 
                                Labels={each.CardFront.type} 
                                chartRadial={each.CardFront.infoRadial}/>*/}
                            </Front>
                            <Back style={{background:each.styles}}>
                                <h4>Additional Informations</h4>
                                <div>
                                    <h4 style={{fontSize:"60px"}}>
                                        {each.CardBack.YesterdayCases}                             
                                    </h4>
                                    <div>{each.CardBack.Name1}</div>
                                    <h4>{each.CardBack.YesVStodRatio}</h4>
                                    <div>{each.CardBack.Name2}</div>
                                </div>
                                <h6>{each.CardBack.SourceProvider}</h6>
                            </Back>
                        </Card>
                    </Wrapper>
                ))
            }
        </Globals>
    )
}

export default GlobalData
