import React from 'react'
import Charts from 'react-apexcharts'

const CardGraph = ({Infos,GData,Type}) => {
    const Data = 
        {
           series:[Infos], 
           options : {
             chart:{
                 height:300,
                 type:'radialbar',
                 offsetX:36,
                 background:"rgb(255, 255, 255, 0.36)",
                 toolbar:{
                     show:false
                 }
             },
             plotOptions:{
                 radialBar:{
                     startAngle:-135,
                     endAngle:225,
                     hollow:{
                         margin:0,
                         size:"83%",
                         imageOffsetX:10,
                         imageOffsetY:70,
                         position:'front',
                         dropShadow:{
                             enabled:true,
                             top:3,
                             left:0,
                             blur:4,
                             opacity:0.24
                         }
                     },
                     track:{
                         background:'#fff',
                         strokeWidth:'47%',
                         margin:0,
                         dropShadow:{
                             enabled:true,
                             top:-3,
                             left:0,
                             blur:10,
                             opacity:0.35
                         }
                     },
                    dataLabels:{
                        show:true,
                        name:{
                            offsetY: -10,
                            show:true,
                            color:'#111',
                            fontSize:'17px',
                            fontFamily:"'Montserrat', sans-serif",
                            fontWeight:200
                        },
                        value:{
                            color:"#111",
                            fontSize:"36px",
                            show:true,
                            fontWeight:200,
                            fontFamily:"'Montserrat', sans-serif",
                        }   
                    }
                 }
             },
             fill:{
                 type:'gradient',
                 gradient:{
                     shade:'Light',
                     type:'hortizontal',
                     shadeintensity:0.5,
                     gradientToColors: ['#e53935','#e35d5b'],
                     inverseColors:true,
                     opacityFrom:1,
                     opacityTo:1,
                     stops:[0,100]
                 }
             },
             stroke:{
                 lineCap:'round'
             },
             labels:[`${Type} : ${GData}`]
        }
    }
    return (
        <div style={{borderRadius:"0.5rem"}}>
            <Charts options={Data.options} series={Data.series}  width="300" height="300" type='radialBar' />
        </div>
    )
}

export default CardGraph
