import React, { useState, useEffect } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'
import { MDBDataTableV5 } from 'mdbreact'

const Tables = () => {
    const [CountryData,setCdata] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        covidURL.get(`/${date}`)
        .then(resp =>
            {
                setCdata(Object.values(resp.data.dates[`${date}`].countries))
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    const rowdata = CountryData.map(each =>
        {
            return {
                name : `${each.name}`,
                total: `${each.today_confirmed}`,
                active: `${each.today_open_cases}`,
                recovered : `${each.today_recovered}`,
                deaths: `${each.today_deaths}`
            }
        })
    console.log(rowdata)
    const [dataTable] = useState({
        columns: [
            {
              label: 'Country',
              field: 'country',
              width: 150,
              attributes: {
                'aria-controls': 'DataTable',
                'aria-label': 'Name',
              },
            },
            {
              label: 'Total Cases',
              field: 'total',
              width: 270,
            },
            {
              label: 'Active',
              field: 'active',
              width: 200,
            },
            {
              label: 'Recovered',
              field: 'recovered',
              sort: 'asc',
              width: 100,
            },
            {
              label: 'Deaths',
              field: 'deaths',
              sort: 'disabled',
              width: 150,
            }
          ],
        rows:[
            {

            }
        ]
    })
    return (
       <TableConatiner>
           <MDBtableWrapper>
                <MDBDataTableV5 borderless materialSearch data={dataTable}/>
           </MDBtableWrapper>
           <MapWrapper>
           </MapWrapper>
       </TableConatiner>
    )
}

export default Tables

const TableConatiner = styled.div`
display:flex;
flex-wrap:wrap;
flex-flow:row;
justify-content:space-between;
width:100%;`
const MDBtableWrapper = styled.div`
flex:1.1;
background-color:pink;`
const MapWrapper = styled.div`
flex:1;
background-color:yellow;`


