import React, { useState, useEffect } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'
import { MDBDataTableV5 } from 'mdbreact'

const Tables = () => {
    const [CountryData,setCdata] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        covidURL.get(`/${date}`)
        .then(resp =>
            {
                setCdata(Object.values(resp.data.dates[`${date}`].countries))
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    const [dataTable] = useState({
        coulmns:[
            {
                label: 'Country',
                field: 'country',
                width: 150,
                attributes: {
                  'aria-controls': 'DataTable',
                  'aria-label': 'Country',
                },
              },
              {
                label: 'Total Cases',
                field: 'total',
                sort: 'asc',
                width: 200,
              },
              {
                label: 'Active',
                field: 'active',
                width: 200,
                sort: 'asc'
              },
              {
                label: 'Recovered',
                field: 'recov',
                sort: 'asc',
                width: 200,
              },
              {
                label: 'Deaths',
                field: 'date',
                width: 150,
                sort: 'asc'
              }
        ],
        rows : CountryData.map((each) =>
        (
            {
                country: each.name,
                total: each.today_confirmed,
                active: each.today_open_cases,
                recov : each
            }
        ))
    })
    return (
       <TableConatiner>
           <MDBtableWrapper>
                <MDBDataTableV5 borderless materialSearch data={dataTable}/>
           </MDBtableWrapper>
           <MapWrapper>

           </MapWrapper>
       </TableConatiner>
    )
}

export default Tables

const TableConatiner = styled.div`
display:flex;
flex-wrap:wrap;
flex-flow:row;
justify-content:space-between;
width:100%;`
const MDBtableWrapper = styled.div`
flex:1.1;
background-color:pink;`
const MapWrapper = styled.div`
flex:1;
background-color:yellow;`
