import React from 'react'
import ReactApexChart from 'react-apexcharts'

const AreaChartCard = () => {

    const data ={
        series: [{
            name: "STOCK ABC",
            data: [16,48,56,9]
          }],
          options: {
            chart: {
              type: 'area',
              height: 350,
              zoom: {
                enabled: false
              }
            },
            dataLabels: {
              enabled: false
            },
            stroke: {
              curve: 'straight'
            },

            title: {
              text: 'Fundamental Analysis of Stocks',
              align: 'left'
            },
            subtitle: {
              text: 'Price Movements',
              align: 'left'
            },
            labels: [9,10,11,12],
            xaxis: {
              type: 'datetime',
            },
            yaxis: {
              opposite: true
            },
            legend: {
              horizontalAlign: 'left'
            }
          },


    }
    return (
        <ReactApexChart options={data.options} series={data.series} />
    )
}

export default AreaChartCard
