import React from 'react'
import ReactApexChart from 'react-apexcharts'

const Donut = () => {

    const series = [44, 55, 41, 17]
    const options = {
        //colors: ['#e54443', '#f46527', '#56CEB1', '#3F6484'],
          chart: {
            type: 'donut',
          },
          plotOptions :{
            pie : {
                startAngle : 0,
                offsetX : 40,
                offsetY : 32,
                customScale : 1.1,
            },
            donut:{
              background : 'transparent',
              labels : false
            }
          },
          dataLabels : {
            enabled : false
          },
          stroke : {
            show : false,
          },
          fill : {
            colors : ["pink" ,"white", "black","red"]
          },
          responsive: [{
            breakpoint: 480,
            options: {
              chart: {
                width: "100%",
                toolbar :{
                    show:false
                }
              },
              dataLabels : {
                  enabled : false
              },
              legend: {
                show : false
              }
            }
          }]
        }
    return (
        <ReactApexChart options={options} series={series} type="donut" width="100%"/>
    )
}

export default Donut
