import React, { useState } from 'react'
import Charts from 'react-apexcharts'

const CardGraph = ({Infos,Labels,chartRadial}) => {
    const [Data] = useState(
        {
           series:[chartRadial], 
           options : {
             chart:{
                 height:300,
                 type:'radialbar',
                 offsetX:70,
                 toolbar:{
                     show:false
                 }
             },
             plotOptions:{
                 radialBar:{
                     startAngle:-135,
                     endAngle:225,
                     hollow:{
                         margin:0,
                         size:"83%",
                         imageOffsetX:0,
                         imageOffsetY:0,
                         position:'front',
                         dropShadow:{
                             enabled:true,
                             top:3,
                             left:0,
                             blur:4,
                             opacity:0.24
                         }
                     },
                     track:{
                         background:'#fff',
                         strokeWidth:'47%',
                         margin:0,
                         dropShadow:{
                             enabled:true,
                             top:-3,
                             left:0,
                             blur:10,
                             opacity:0.35
                         }
                     },
                    dataLabels:{
                        show:true,
                        name:{
                            offsetY: -10,
                            show:true,
                            color:'#111',
                            fontSize:'17px',
                            fontFamily:"'Montserrat', sans-serif",
                            fontWeight:200
                        },
                        value:{
                            formatter:(val =>
                                {
                                    return parseInt(val)
                                }),
                            color:"#111",
                            fontSize:"36px",
                            show:true,
                            fontWeight:200,
                            fontFamily:"'Montserrat', sans-serif",
                        }   
                    }
                 }
             },
             fill:{
                 type:'gradient',
                 gradient:{
                     shade:'Light',
                     type:'hortizontal',
                     shadeintensity:0.5,
                     gradientToColors: ['#fff'],
                     inverseColors:true,
                     opacityFrom:1,
                     opacityTo:1,
                     stops:[0,100]
                 }
             },
             stroke:{
                 lineCap:'round'
             },
             labels:[Labels]
        }
    }
    )
    return (
        <div style={{background:"rgba(255, 191, 191, 0.36)",borderRadius:"0.5rem"}}>
            <Charts options={Data.options} series={Data.series}  width="300" height="300" type='radialBar' />
        </div>
    )
}

export default CardGraph
