import React, { useEffect, useState } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'

const GlobalData = () => {
    const Cards=[
        {
            styles:"background:"
        },
        {
            styles:"background:linear-gradient(to right, #ff512f, #f09819)"
        },
        {
            styles:"background:linear-gradient(to right, #4cb8c4, #3cd3ad)"
        },
        {
            styles:"background:linear-gradient(to right, #485563, #29323c)"
        }
    ]
    const bgColor=["linear-gradient(to right, #e53935, #e35d5b)"]
    const Globals = styled.div`
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    justify-content:space-between;
    `
    const Wrapper= styled.div`
      perspective:1000rem;
      position:relative;
      transition:0.8s all;
      height:400px;
      width:400px;
    `
    const Card= styled.div`
    width:100%;
    height:100%;
    position:absolute;
    backface-visibility:hidden;
    transform-style:preserve-3d;
    transition:1s all;
    &:hover{
        transform: rotateY(180deg);
    }
    `
    const Front = styled.div`
    position:absolute;
    backface-visibility:hidden;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    border-radius:0.5rem;
    `

    const Back = styled.div`
    position:absolute;
    backface-visibility:hidden;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    transform:rotateY(180deg);
    border-radius:0.5rem;
    background:yellow;
    `

    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    const [GlobalInfo,setData] = useState([])
    useEffect(() =>
    {
        covidURL.get(`/${date}/country/India`)
        .then(resp =>
            {
                setData(resp.data.total)
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    return (
        <Globals>
            {
                Cards.map((each,index) =>
                (
                    <Wrapper>
                        <Card>
                            <Front style={{}}>Front</Front>
                            <Back>Back</Back>
                        </Card>
                    </Wrapper>
                ))
            }
        </Globals>
    )
}

export default GlobalData
