import React, { useEffect, useState } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'

const GlobalData = () => {
    const Cards=[
        {
            background:"linear-gradient(to right, #e53935, #e35d5b)"
        },
        {
            background:"linear-gradient(to right, #ff512f, #f09819)"
        },
        {
            background:"linear-gradient(to right, #4cb8c4, #3cd3ad)"
        },
        {
            background:"linear-gradient(to right, #1f1c2c, #928dab)"
        }
    ]
    const Globals = styled.div`
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    justify-content:space-between;
    `
    const Card= styled.div`
    height:400px;
    width:400px;
    border-radius:0.5rem;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    background:white;
    justify-content:center;
    `
    const Wrapper= styled.div`
    perspective:1000rem;
    position:relative;`
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    const [GlobalInfo,setData] = useState([])
    useEffect(() =>
    {
        covidURL.get(`/${date}/country/India`)
        .then(resp =>
            {
                setData(resp.data.total)
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    return (
        <Globals>
            {
                Cards.map((each,index) =>
                (
                    <Wrapper>
                        <Card>ggh</Card>
                        <Card style={{transform:"rotateY()"}}>Hi</Card>
                    </Wrapper>
                ))
            }
        </Globals>
    )
}

export default GlobalData
