import React from 'react'
import styled from 'styled-components'
const Nav = styled.nav`
display:flex;
flex-direction:row;
flex-wrap:wrap;
justify-content:space-between;
font-weight:200;
font-size:40px;`
const Dates = styled.span`
font-size`
export const CurrentDate = () =>
{   const months= ["January","February","March","April","May","June","July","August","September","October","November","December"];
    const days=["Sunday","Monday","Tuesday","Wedensday","Thursday","Friday","Saturday"]
    return(
    <>
        <span>{days[new Date().getDay()]} {new Date().getDate()}<sup>th</sup></span>
        <span>{months[new Date().getMonth()]}{new Date().getFullYear()}</span>
    </>
    )
}
const Header = () => {
    return (
        <Nav>
            <div className="Logo">Covlive</div>
            <div className="Date"><CurrentDate /></div>
        </Nav>
    )
}

export default Header
