import React from 'react'
import ReactApexChart from 'react-apexcharts'

const Donut = () => {

    const series = [44, 55, 41, 17, 15]
    const options = {
          chart: {
            type: 'donut',
          },
          responsive: [{
            breakpoint: 480,
            options: {
              chart: {
                width: "100%",
                toolbar :{
                    show:false
                }
              },
              dataLabels : {
                  enabled : false
              },
              legend: {
                show : false
              }
            }
          }]
        }
    return (
        <ReactApexChart options={options} series={series} type="donut" width="100%" />
    )
}

export default Donut
