import React, { useEffect, useState } from 'react'
import { GlobalUrl } from '../../axios'
import styled from 'styled-components'
//import CardGraph from './CardGraph/CardGraph'

const GlobalData = () => {
    const [GlobalInfo,setData] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        GlobalUrl.get(``)
        .then(resp =>
            {
                setData(resp.data)
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    const Cards=[
        {
            styles:"linear-gradient(to right, #e53935, #e35d5b)",
            CardFront:{
                type:"Total"
            },
            CardBack:{
                affectedCountries:GlobalInfo.affectedCountries,
                critical:GlobalInfo.critical,
                criticalPerOneMillion:GlobalInfo.criticalPerOneMillion,
                type:"Cases per million",
                PerOneMillion: GlobalInfo.casesPerOneMillion,
            }
        },
        {
            styles:"linear-gradient(to right, #ff512f, #f09819)",
            CardFront:{
                type:"Active",
            },
            CardBack:{
                type:"Active per million",
                PerOneMillion:GlobalInfo.activePerOneMillion,
            }
        },
        {
            styles:"linear-gradient(to right, #4cb8c4, #3cd3ad)",
            CardFront:{
                type:"Recovered",

            },
            CardBack:{
               type:"Recovered per million",
               PerOneMillion:GlobalInfo.recoveredPerOneMillion
            }
        },
        {
            styles:"linear-gradient(to right, #606c88, #3f4c6b)",
            CardFront:{
                type:"Deaths",

            },
            CardBack:{
               type:"Deaths per million",
               PerOneMillion:GlobalInfo.deathsPerOneMillion
            }
        }
    ]
    const Globals = styled.div`
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    justify-content:space-between;
    `
    const Wrapper= styled.div`
      perspective:1000rem;
      position:relative;
      transition:0.8s all;
      height:400px;
      width:400px;
      margin-bottom:20px;
    `
    const Card= styled.div`
    width:100%;
    height:100%;
    position:absolute;
    backface-visibility:hidden;
    transform-style:preserve-3d;
    transition:1s all;
    &:active{
        transform: rotateY(180deg);
    }
    `
    const Front = styled.div`
    position:absolute;
    backface-visibility:hidden;
    padding:15px;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:center;
    border-radius:0.5rem;
    `

    const Back = styled.div`
    position:absolute;
    backface-visibility:hidden;
    color:black;
    width:100%;
    height:100%;
    display:flex;
    padding:15px;
    flex-direction:column;
    flex-wrap:wrap;
    justify-content:space-between;
    text-align:center;
    transform:rotateY(-180deg);
    border-radius:0.5rem;
    h4
    {
        font-weight: 700;
    }
    div
    {

    }
    `
    return (
        <Globals>
            {   
                Cards.map((each,index) =>
                (   

                    <Wrapper key={index}>
                        <Card>
                            <Front style={{background:each.styles}}>

                            </Front>
                            <Back style={{background:each.styles}}>
                               <h4>Aditional Infos</h4>
                               <div style={{textAlign:"justify",background:"white"}}>
                                   <div style={{width:"fit-content"}}>
                                    <h3>{each.CardBack.type}</h3>
                                    <div>{each.CardBack.PerOneMillion}</div>
                                   </div>
                               </div>
                            </Back>
                        </Card>
                    </Wrapper>
                ))
            }
        </Globals>
    )
}

export default GlobalData
