import React, { useState, useEffect } from 'react'
import { covidURL } from '../../axios'
import styled from 'styled-components'
import { MDBTable, MDBTableHead, MDBTableBody } from 'mdbreact'

const Tables = () => {
    const [CountryData,setCdata] = useState([])
    const date =`${new Date().getFullYear()}-${"0" + (new Date().getMonth() + 1)}-${new Date().getUTCDate()}`
    useEffect(() =>
    {
        covidURL.get(`/${date}`)
        .then(resp =>
            {
                setCdata(Object.values(resp.data.dates[`${date}`].countries))
            })
        .catch(err =>
            {
                console.log(err)
            })
    },[date])
    return (
       <TableConatiner>
           <MDBtableWrapper>
                <MDBTable borderless>
                    <MDBTableHead>
                        <tr>
                            <th>Country</th>
                            <th>Total Cases</th>
                            <th>Active</th>
                            <th>Recovered</th>
                            <th>Deaths</th>
                        </tr>
                    </MDBTableHead>
                    <MDBTableBody>
                       {
                           CountryData.map((each,index) =>
                           {
                               return <tr key={index}>
                                   <td>{each.name}</td>
                                   <td>{each.today_confirmed}</td>
                                   <td>{each.today_open_cases}</td>
                                   <td>{each.today_recovered}</td>
                                   <td>{each.
today_deaths}</td>
                               </tr>
                           }) 
                       }
                    </MDBTableBody>
                </MDBTable>
           </MDBtableWrapper>
           <MapWrapper>

           </MapWrapper>
       </TableConatiner>
    )
}

export default Tables

const TableConatiner = styled.div`
display:flex;
flex-wrap:wrap;
flex-flow:row;
justify-content:space-between;
width:100%;`
const MDBtableWrapper = styled.div`
flex:1.1;
background-color:pink;`
const MapWrapper = styled.div`
flex:1;
background-color:yellow;`
